import React, {FC } from 'react';
import {
    Typography,
    Toolbar,
    Grid,
    Paper,
    makeStyles,
    Avatar,
    Select,
    FormControl,
    MenuItem,
  } from "@material-ui/core";

interface Props {
    handleChange?:(e:any)=>void;
    name?: any;
    value?: any;
    handleBlur?:(e: React.FocusEvent<HTMLInputElement>)=>void;
    fullWidth?:any;
    input:any;
}

const Dropdown: FC<Props> = ({value,handleChange,name,handleBlur,fullWidth,input }) => {
    return (
        <>
        <Select
          value={value}
          onChange={handleChange}
          onBlur={handleBlur}
          name={name}
          variant="outlined"
           style={{width:"100%"}}
        >


{input.list.map((item: any) => {
                                    return (
                                      <MenuItem value={item.name}>
                                        <Typography
                                          style={{ fontSize: "12px" }}
                                        >
                                          {item.name}
                                        </Typography>
                                      </MenuItem>
                                    );
                                  })}
        </Select>
        </>
    )
}
export default Dropdown;
